    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        //if ((tTime.getText()).equals("") && (tId.getText()).equals("") && (tName.getText()).equals("") && (tType.getText()).equals("") && (tTopage.getText()).equals("") && (tDist.getText()).equals("")) {
        if (!((tId.getText()).equals("") || (tName.getText()).equals("") || (tType.getText()).equals("") || (tTopage.getText()).equals("") || (tDist.getText()).equals("") || (tTime.getText()).equals(""))) {
            String sql = "INSERT INTO train(T_ID, T_Name, T_Type, T_Ttopage, T_Destination, T_Time) VALUES (?,?,?,?,?,?)";

            try {
                pst = con.prepareStatement(sql);
                pst.setString(1, tId.getText());
                pst.setString(2, tName.getText());
                pst.setString(3, tType.getText());
                pst.setString(4, tTopage.getText());
                pst.setString(5, tDist.getText());
                pst.setString(6, tTime.getText());
                pst.execute();
                //Bogie bo=new Bogie(tIdd);
                JOptionPane.showMessageDialog(null, "Insert Successfully.");

                /*
            Trains tr=new Trains();
            tr.setVisible(true);
            dispose();
                 */
                tableUpdate();

                tId.setText("");
                tName.setText("");
                tType.setText("");
                tTopage.setText("");
                tDist.setText("");
                tTime.setText("");
                tId.requestFocus();

            } catch (Exception ex) {
                JOptionPane.showMessageDialog(null, "Internal problem.");
            }

        } else {
            JOptionPane.showMessageDialog(null, "Fill all the boxes.");
        }

    }//GEN-LAST:event_jButton1ActionPerformed

