    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        // TODO add your handling code here:
        //if(tId.getText()!="" && tName.getText()!="" && tType.getText()!="" && tTopage.getText()!="" && tDist.getText("")!="" && tTime.getText()!="")

        String sql = "SELECT * FROM train WHERE T_ID=?";

        try {
            pst = con.prepareStatement(sql);
            pst.setString(1, tId.getText());
            rs = pst.executeQuery();

            //JOptionPane.showMessageDialog(null, ""+rs);
            if (rs.next() && !(tId.getText()).equals("")) {
                //JOptionPane.showMessageDialog(null, "Log IN Successfully.");

                tIdd = tId.getText();
                Bogie bo = new Bogie(tId.getText());
                bo.setVisible(true);
                dispose();
            } else if ((tId.getText()).equals("")) {
                JOptionPane.showMessageDialog(null, "Select a Train.");
            } else {
                JOptionPane.showMessageDialog(null, "Not Found.");
            }
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, ex);
        }
    }//GEN-LAST:event_jButton5ActionPerformed

